import { createStore, combineReducers } from 'redux';
import { Campsites } from './campsites';
import { Partners } from './partners';
import { Promotions } from './promotions';

export const ConfigureStore = () => {
    const store = createStore(
        combineReducers({
            campsites: Campsites,
            comment
        })
    );

    return store;
};
